import Form from 'react-bootstrap/Form'
import {useEffect, useState} from "react";
import {useNavigate} from "react-router-dom";


export default function CreateBoard({ appState, setAppState }){
    let navigate = useNavigate();

    function createBoard(event){
        event.preventDefault();
        console.log("Board Name value when clicking the button is: " + boardName);
        let board = {
            name: boardName,
            columns: [],
        }
        setAppState({...appState, boards: appState.boards.concat([board])});
        localStorage.setItem("board", JSON.stringify(board));
        console.log("Json stored in local storage is: " + localStorage.getItem("board"));
        navigate('/home');
    }

    const [boardName, setBoardName] = useState('');


    function handleChange(event){
        console.log("target value: " + event.target.value);
        setBoardName(event.target.value);
        console.log("board name: " + boardName);
    }

    return(
        <Form>
            <Form.Group className="boardInputName" controlId="boardInputName">
                <Form.Control type="text" placeholder="Board name" onChange={handleChange} value={boardName} />
            </Form.Group>
            <button variant="primary" type="submit" onClick={createBoard}>
                Submit
            </button>
        </Form>
    )
}
