import Form from 'react-bootstrap/Form'

export default function CreateBoard({ appState, setAppState }){
    function createBoard(){
        let board = {
            name: "",
            columns: [],
        }
        setAppState({...appState, boards: appState.boards.concat([board])});
    }

    return(
        <Form>
            <Form.Group className="boardInputName" controlId="boardInputName">
                <Form.Control type="text" placeholder="Board name" />
            </Form.Group>
            <button variant="primary" type="submit">
                Submit
            </button>
        </Form>
    )
}
