import React from 'react';
import CardModal from '../modals/cardModal';

export default function Card({ card, cardIndex, columnIndex, appState, setAppState }) {

    const [isCardModalOpen, setCardModalOpen] = setAppState

    return (
    <div className='card'>
        <p>{card.name}</p>
        <i className='fas fa-pen' ></i>
        <CardModal card={card} cardIndex={cardIndex} columnIndex={columnIndex} appState={appState} setAppState={setAppState}/>
    </div>)

}
